import React from 'react';

import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
} from 'react-native';

const Estilos = StyleSheet.create({
  titulo: { fontWeight: 'bold', fontSize: '20pt', textAlign: 'center' },
  subtitulo: { fontWeight: 'bold', fontSize: '15pt', textAlign: 'center' },
  container: { backgroundColor: '#dddddd' },
});

const App = () => {
  return (
    <ScrollView>
      <Text style={Estilos.titulo}>Sistema Solar</Text>

      <View>
        <Text style={Estilos.subtitulo}>Planeta Terra</Text>
      </View>
    </ScrollView>
  );
};

export default App;
