import React from 'react';
import { View, Text } from 'react-native';

const App = () => {
  return (
    <View
      style={{ backgroundColor: '#dddddd', flex: 1, justifyContent: 'center' }}
    >
      <Text>Alô Mundo da Programação</Text>
    </View>
  );
};

export default App;
