import React from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  ScrollView,
  Image,
  TouchableOpacity,
} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

const styles = StyleSheet.create({
  brasil: {
    width: 181,
    height: 305,
  },
  textBrasil: {
    fontWeight: 'bold',
    fontSize: 20,
  },
  alemanha: {
    width: 181,
    height: 233,
  },
  textAlemanha: {
    fontWeight: 'bold',
    fontSize: 20,
    color: '#fff',
  },
  italia: {
    width: 181,
    height: 233,
  },
  argentina: {
    width: 181,
    height: 233,
  },
});

const TelaInicial = ({ navigation }) => {
  return (
    <View style={{ flexDirection: 'column', alignItems: 'center' }}>
      <TouchableOpacity
        style={{
          alignItems: 'center',
          padding: 20,
          backgroundColor: '#FFFF01',
          marginBottom: 10,
        }}
        onPress={() => navigation.navigate('Brasil')}
      >
        <Image style={styles.brasil} source={require('./assets/brasil.png')} />
        <Text style={styles.textBrasil}>Seleção Brasileira</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={{
          alignItems: 'center',
          padding: 20,
          backgroundColor: '#808080',
          marginBottom: 10,
        }}
        onPress={() => navigation.navigate('Alemanha')}
      >
        <Image
          style={styles.alemanha}
          source={require('./assets/alemanha.png')}
        />
        <Text style={styles.textAlemanha}>Seleção Alemã</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={{
          alignItems: 'center',
          padding: 20,
          backgroundColor: '#0000FE',
          marginBottom: 10,
        }}
        onPress={() => navigation.navigate('Italia')}
      >
        <Image style={styles.italia} source={require('./assets/italia.png')} />
        <Text style={styles.textAlemanha}>Seleção Italiana</Text>
      </TouchableOpacity>

      <Button title="Sobre" onPress={() => navigation.navigate('Sobre')} />
    </View>
  );
};

const TelaSobre = () => {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Meu App!!!</Text>
      <Text>Versão 1.0</Text>
      <Text>Desenvolvido por Jota Pê</Text>
    </View>
  );
};

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Início">
        <Stack.Screen name="Seleções" component={TelaInicial} />
        <Stack.Screen name="Sobre" component={TelaSobre} />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
