import React from 'react';

import { View, Text, SectionList, StyleSheet } from 'react-native';

const Estilos = StyleSheet.create({
  container: { flex: 1, paddingTop: 25, backgroundColor: 'silver' },

  titulo: {
    fontSize: 28,
    fontWeight: 'bold',
    paddingTop: 25,
    textAlign: 'center',
  },

  item: { padding: 10, fontSize: 18, height: 40, borderBottomWidth: 1 },

  tituloSecao: {
    fontSize: 20,
    fontWeight: 'bold',
    fontStyle: 'italic',
    backgroundColor: 'gray',
    color: 'white',
  },
});

const App = () => {
  return (
    <View style={Estilos.container}>
      <Text style={Estilos.titulo}>Lista de Bandas</Text>

      <SectionList
        sections={[
          {
            title: 'Rock Internacional',
            data: [
              'Oasis',
              'The Cure',
              'Guns And Roses',
              'The Smiths',
              'The Beatles',
            ],
          },
        ]}
        renderItem={({ item }) => <Text style={Estilos.item}>{item}</Text>}
        renderSectionHeader={({ section }) => (
          <Text style={Estilos.tituloSecao}>{section.title}</Text>
        )}
      />
    </View>
  );
};

export default App;
