import { StyleSheet, View, Text, TextInput } from 'react-native';

const Estilos = StyleSheet.create({
  container: { padding: 10 },
  titulo: { fontSize: 24, fontWeight: 'bold', marginTop: 10 },
  paragrafo: { fontSize: 14, marginTop: 10, lineHeight: 17 },
});

const Logotipo = {
  uri: 'http://upload.wikimedia.org/wikipedia/commons/7/73/Javascript-736400_960_720.png',
};

const App = () => {
  const [texto, setTexto] = useState('teste');

  return (
    <View style={Estilos.container}>
      <Text style={Estilos.texto}>
        {texto.split(' ').map((palavra) => palavra && '***')}
      </Text>
    </View>
  );
};

export default App;
