import React from 'react';

import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
} from 'react-native';

const Estilos = StyleSheet.create({
  titulo: { fontWeight: 'bold', fontSize: '20pt', textAlign: 'center' },
  subtitulo: { fontWeight: 'bold', fontSize: '15pt', textAlign: 'center' },
  container: { backgroundColor: '#dddddd' },
  imagem: { width: 200, height: 200, marginTop: 10, textAlign: 'center' },
});

const App = () => {
  return (
    <ScrollView>
      <Text style={Estilos.titulo}>Sistema Solar</Text>

      <View>
        <Text style={Estilos.subtitulo}>Planeta Terra</Text>
        <Image
          style={Estilos.imagem}
          source={{
            uri: 'https://upload.wikimedia.org/wikipedia/commons/4/4e/Blue_Marble_Eastern_Hemisphere.jpg',
          }}
        />
      </View>
    </ScrollView>
  );
};

export default App;
