import React, { useState } from 'react';
import { StyleSheet, View, Text, FlatList } from 'react-native';

const Estilos = StyleSheet.create({
  container: { padding: 10 },
  titulo: { fontSize: 24, fontWeight: 'bold', marginTop: 10 },
  paragrafo: {
    fontSize: 14,
    marginTop: 10,
    lineHeight: 17,
    textAlign: 'justify',
  },
  imagem: { marginTop: 20, alignSelf: 'center' },
});

const Logotipo = {
  uri: 'https://upload.wikimedia.org/wikipedia/commons/7/73/Javascript-736400_960_720.png',
  width: 150,
  height: 150,
};

const App = () => {
  const [texto, setTexto] = useState('teste');

  return (
    <ScrollView style={Estilos.container}>
      <Text style={Estilos.texto}>Aprenda JavaScript</Text>
      <Text style={Estilos.paragrafo}>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc efficitur
        fringilla purus eu condimentum. Suspendisse tincidunt egestas purus, id
        dictum orci commodo non. Integer et elit dapibus, pharetra nisl viverra,
        lacinia felis. Morbi varius scelerisque magna, non iaculis sapien
        pulvinar eget. Donec ex erat, laoreet eu urna ac, tincidunt consequat
        dui. Proin sodales lacinia felis at dapibus. Etiam eget augue eget nisl
        ornare vehicula eget in sapien. Class aptent taciti sociosqu ad litora
        torquent per conubia nostra, per inceptos himenaeos. Quisque imperdiet
        faucibus libero. Pellentesque habitant morbi tristique senectus et netus
        et malesuada fames ac turpis egestas. Vivamus consequat tristique est,
        non dapibus nulla ultricies sed. Mauris ex turpis, congue ac rutrum
        consectetur, dapibus sed lacus.
      </Text>
      <Image source={Logotipo} style={Estilos.imagem} />
    </ScrollView>
  );
};

export default App;
